      + "GROUP BY deptno ORDER BY deptno, ^sum(empno)^",
      "Expression 'SUM\\(`EMP`\\.`EMPNO`\\)' is not in the select clause");
  checkFails("SELECT DISTINCT deptno, min(empno) from emp\n"
      + "GROUP BY deptno ORDER BY deptno, ^sum(empno)^",
      "Expression 'SUM\\(`EMP`\\.`EMPNO`\\)' is not in the select clause");
  check("SELECT DISTINCT deptno, sum(empno) from emp\n"
      + "GROUP BY deptno ORDER BY deptno, sum(empno)");
}

