         createBuffer(2),

        
         createBarrier(5, 2),

        
         createBuffer(1),
        createBuffer(0),

        
         createCancellationBarrier(3, 0),

        
         createBuffer(0),
        createBuffer(1),

        
         createBarrier(5, 0),
        createBarrier(5, 1),

        
         createBuffer(0),
        createBuffer(1),
        createBuffer(2)
