            .authorizedTo(Job.READ, Job.CONFIGURE, Jenkins.READ)
            .invokeWithArgs("aProject");

    assertThat(result, succeededSilently());

    assertThat(project.scheduleBuild2(0).get().getLog(), containsString("echo 2"));
}

