public void assertReadLongStringValue() {
    String expected = "test_value";
    columnDef.setColumnMeta((MySQLColumnType.MYSQL_TYPE_STRING.getValue() ^ ((256 & 0x300) >> 4)) << 8);
    when(payload.getByteBuf()).thenReturn(byteBuf);
