  List<List<Tuple2<Integer, String>>> expected = Arrays.asList(
    Arrays.asList(
      new Tuple2<>(1, "california"),
      new Tuple2<>(3, "california"),
      new Tuple2<>(4, "new york"),
      new Tuple2<>(1, "new york")),
    Arrays.asList(
      new Tuple2<>(5, "california"),
