protected synchronized void startInternal() throws LifecycleException {

    if(log.isDebugEnabled())
        log.debug("Starting " + ("".equals(getName()) ? "ROOT" : getName()));

    
    preRegisterJMX();

    if ((oname != null) && 
        (Registry.getRegistry(null, null).getMBeanServer().isRegistered(oname))) {
        
        
        Registry.getRegistry(null, null).unregisterComponent(oname);
    }

    setConfigured(false);
    boolean ok = true;

    
    if (webappResources == null) {   
        if (log.isDebugEnabled())
            log.debug("Configuring default Resources");
        try {
            if ((docBase != null) && (docBase.endsWith(".war")) && (!(new File(getBasePath())).isDirectory()))
                setResources(new WARDirContext());
            else
                setResources(new FileDirContext());
        } catch (IllegalArgumentException e) {
            log.error("Error initializing resources: " + e.getMessage());
            ok = false;
        }
    }
    if (ok) {
        if (!resourcesStart()) {
            log.error( "Error in resourceStart()");
            ok = false;
        }
    }

    
    
    
    if( realm == null && mserver != null ) {
        ObjectName realmName=null;
        try {
            realmName=new ObjectName( getEngineName() + ":type=Realm,host=" + 
                    getHostname() + ",path=" + getPath());
            if( mserver.isRegistered(realmName ) ) {
                mserver.invoke(realmName, "init", 
                        new Object[] {},
                        new String[] {}
                );            
            }
        } catch( Throwable t ) {
            if(log.isDebugEnabled())
                log.debug("No realm for this host " + realmName);
        }
    }

    if (getLoader() == null) {
        WebappLoader webappLoader = new WebappLoader(getParentClassLoader());
        webappLoader.setDelegate(getDelegate());
        setLoader(webappLoader);
    }

    
    getCharsetMapper();

    
    postWorkDirectory();

    
    boolean dependencyCheck = true;
    try {
        dependencyCheck = ExtensionValidator.validateApplication
            (getResources(), this);
    } catch (IOException ioe) {
        log.error("Error in dependencyCheck", ioe);
        dependencyCheck = false;
    }

    if (!dependencyCheck) {
        
        ok = false;
    }

    
    String useNamingProperty = System.getProperty("catalina.useNaming");
    if ((useNamingProperty != null)
        && (useNamingProperty.equals("false"))) {
        useNaming = false;
    }

    if (ok && isUseNaming()) {
        if (namingContextListener == null) {
            namingContextListener = new NamingContextListener();
            namingContextListener.setName(getNamingContextName());
            addLifecycleListener(namingContextListener);
        }
    }

    
    if (log.isDebugEnabled())
        log.debug("Processing standard container startup");


    
    ClassLoader oldCCL = bindThread();

    boolean mainOk = false;

    try {

        if (ok) {

            
            if ((loader != null) && (loader instanceof Lifecycle))
                ((Lifecycle) loader).start();

            
            unbindThread(oldCCL);

            
            oldCCL = bindThread();

            
            
            logger = null;
            getLogger();
            if ((logger != null) && (logger instanceof Lifecycle))
                ((Lifecycle) logger).start();

            if ((cluster != null) && (cluster instanceof Lifecycle))
                ((Lifecycle) cluster).start();
            if ((realm != null) && (realm instanceof Lifecycle))
                ((Lifecycle) realm).start();
            if ((resources != null) && (resources instanceof Lifecycle))
                ((Lifecycle) resources).start();

            
            fireLifecycleEvent(Lifecycle.CONFIGURE_START_EVENT, null);

            
            Container children[] = findChildren();
            for (int i = 0; i < children.length; i++) {
                children[i].start();
            }

            
            
            if (pipeline instanceof Lifecycle) {
                ((Lifecycle) pipeline).start();
            }

            
            Manager contextManager = null;
            if (manager == null) {
                if ( (getCluster() != null) && distributable) {
                    try {
                        contextManager = getCluster().createManager(getName());
                    } catch (Exception ex) {
                        log.error("standardContext.clusterFail", ex);
                        ok = false;
                    }
                } else {
                    contextManager = new StandardManager();
                }
            } 

            
            if (contextManager != null) {
                setManager(contextManager);
            }

            if (manager!=null && (getCluster() != null) && distributable) {
                
                
                getCluster().registerManager(manager);
            }


            mainOk = true;

        }

    } finally {
        
        unbindThread(oldCCL);
        if (!mainOk) {
            
            
            registerJMX();
        }
    }

    if (!getConfigured()) {
        log.error( "Error getConfigured");
        ok = false;
    }

    
    if (ok)
        getServletContext().setAttribute
            (Globals.RESOURCES_ATTR, getResources());

    
    mapper.setContext(getPath(), welcomeFiles, resources);

    
    oldCCL = bindThread();

    if (ok ) {
        if (instanceManager == null) {
            javax.naming.Context context = null;
            if (isUseNaming() && namingContextListener != null) {
                context = namingContextListener.getEnvContext();
            }
            Map<String, Map<String, String>> injectionMap = 
            	buildInjectionMap(getIgnoreAnnotations() ? new NamingResources(): getNamingResources());
            instanceManager = new DefaultInstanceManager
            	(context, injectionMap, this, this.getClass().getClassLoader());
            getServletContext().setAttribute(InstanceManager.class.getName(), instanceManager);
        }
    }

    try {

        
        if (ok) {
            getServletContext().setAttribute(
                    JarScanner.class.getName(), getJarScanner());
        }

        if (ok) {
            postWelcomeFiles();
        }

        
        for (Map.Entry<ServletContainerInitializer, Set<Class<?>>> entry :
            initializers.entrySet()) {
            try {
                entry.getKey().onStartup(entry.getValue(),
                        getServletContext());
            } catch (ServletException e) {
                
                ok = false;
                break;
            }
        }

        
        if (ok) {
            if (!listenerStart()) {
                log.error( "Error listenerStart");
                ok = false;
            }
        }

        try {
            
            if ((manager != null) && (manager instanceof Lifecycle)) {
                ((Lifecycle) getManager()).start();
            }

            
            super.threadStart();
        } catch(Exception e) {
            log.error("Error manager.start()", e);
            ok = false;
        }

        
        if (ok) {
            if (!filterStart()) {
                log.error( "Error filterStart");
                ok = false;
            }
        }

        
        if (ok) {
            loadOnStartup(findChildren());
        }

    } finally {
        
        unbindThread(oldCCL);
    }

    
    if (ok) {
        if (log.isDebugEnabled())
            log.debug("Starting completed");
    } else {
        log.error(sm.getString("standardContext.startFailed", getName()));
    }

    
    registerJMX();

    startTime=System.currentTimeMillis();

    
    if (ok && (this.getObjectName() != null)) {
        Notification notification = 
            new Notification("j2ee.state.running", this.getObjectName(), 
                            sequenceNumber++);
        broadcaster.sendNotification(notification);
    }

    
    
    if (getLoader() instanceof WebappLoader) {
        ((WebappLoader) getLoader()).closeJARs(true);
    }

    
    if (!ok) {
        setState(LifecycleState.FAILED);
    } else {
        setState(LifecycleState.STARTING);
    }
}

