public void testRecursion() throws Exception {
	XmlSerializerBuilder s = XmlSerializer.create().maxDepth(Integer.MAX_VALUE);

	R1 r1 = new R1();
	R2 r2 = new R2();
	R3 r3 = new R3();
	r1.r2 = r2;
	r2.r3 = r3;
	r3.r1 = r1;

	
	assertThrown(()->s.build().serialize(r1)).contains("It's recommended you use the BeanTraverseContext.BEANTRAVERSE_detectRecursions setting to help locate the loop.");

	
	s.detectRecursions();
	assertThrown(()->s.build().serialize(r1)).contains("[0] <noname>:org.apache.juneau.xml.CommonTest$R1", "->[1] r2:org.apache.juneau.xml.CommonTest$R2", "->[2] r3:org.apache.juneau.xml.CommonTest$R3", "->[3] r1:org.apache.juneau.xml.CommonTest$R1");

	s.ignoreRecursions();
	assertEquals("<object><name>foo</name><r2><name>bar</name><r3><name>baz</name></r3></r2></object>", s.build().serialize(r1));
}

