        assertOutputKeyValue(driver, 1, "X1+null");
        assertNull(driver.readOutput(output));

        
        for (int i = 0; i < 2; i++) {
            driver.pipeInput(recordFactory.create(topic2, expectedKeys[i], "Y" + expectedKeys[i]));
        }
        
