public double[] resize(double[] old, double defaultValue) {
  double[] values = new double[getNewSize()];
  if (defaultValue != 0) {
    Arrays.fill(values, 0, values.length, defaultValue);
  }
  for (int i = 0; i < old.length; i++) {
    double val = old[i];
    if (val != defaultValue) {
      int newSlot = getNewSlot(i);
      if (newSlot >= 0) {
        values[newSlot] = val;
      }
    }
  }
  return values;
}

