@Test public void testUnion1() {
  
  
  
  
  
  
  final RelBuilder builder = RelBuilder.create(config().build());
  RelNode root =
      builder.scan("DEPT")
          .project(builder.field("DEPTNO"))
          .scan("EMP")
          .project(builder.field("EMPNO"))
          .scan("EMP")
          .project(builder.field("DEPTNO"))
          .union(true, 1)
          .build();
  assertThat(str(root),
      is("LogicalProject(DEPTNO=[$7])\n"
          + "  LogicalTableScan(table=[[scott, EMP]])\n"));
}

