  assertThat(aSchema.getSubSchemaNames().size(), is(0));

  
  
  assertThat(aSchema.contentsHaveChangedSince(-1, 1), equalTo(false));
  assertThat(aSchema.contentsHaveChangedSince(1, 1), equalTo(false));

  
