public static Object minusAny(Object b0, Object b1) {
  if (b0 == null || b1 == null) {
    return null;
  }

  if (allAssignable(Number.class, b0, b1)) {
    return minus(toBigDecimal((Number) b0), toBigDecimal((Number) b1));
  }

  throw notArithmetic("-", b0, b1);
}

